basicnumber.script:
function onHover(oncontrol)
	if super and super.onHover then
		Debug.chat("neat");
	end
	if oncontrol then
		local draginfo = Input.getDragData();
		if draginfo and draginfo.isType("number") and draginfo.getNumberData() < getValue() then
			setColor("FF6161");
		end
	else
		setColor(nil);
	end
end

function onDrop(x, y, draginfo)
	if draginfo.isType("number") and draginfo.getNumberData() > getValue() then
		setValue(draginfo.getNumberData());
	end

	return true;
end

stringcontrol.script:
function onClickDown(button, x, y)
	return true;
end
function onClickRelease(button, x, y)
	if isVisible() then	setSource(""); end
end
function onDragStart(button, x, y, draginfo)
	return window.onDragStart(button, x, y, draginfo);
end
function onDrop(x, y, draginfo)
	return window.onDrop(x, y, draginfo);
end
function setSource(sNode)
	if not sNode then sNode = ""; end
	window.source_name.setValue(sNode);
end
